void interchangeFirstLast(int m[][n]) 
{ 
	for (int i = 0; i < n; i++) 
	{ 
		int t = m[i][0]; 
		m[i][0] = m[i][n - 1]; 
		m[i][n - 1] = t; 
	} 
} 

