using namespace std;

const int MAXN = 105;
double a[MAXN][MAXN];

double determinant(int n) {
	double det = 1.0;
	for (int i = 0; i < n; i++) {
		int pivot = i;
		for (int j = i + 1; j < n; j++) {
			if (abs(a[j][i]) > abs(a[pivot][i])) {
				pivot = j;
			}
		}
		if (pivot != i) {
			swap(a[i], a[pivot]);
			det *= -1;
		}
		if (a[i][i] == 0) {
			return 0;
		}
		det *= a[i][i];
		for (int j = i + 1; j < n; j++) {
			double factor = a[j][i] / a[i][i];
			for (int k = i + 1; k < n; k++) {
				a[j][k] -= factor * a[i][k];
			}
		}
	}
	return det;
}

int main() {
	int n = 4;
	double matrix[4][4] = {{1, 0, 2, -1},
						{3, 0, 0, 5},
						{2, 1, 4, -3},
						{1, 0, 5, 0}};
	for (int i = 0; i < n; i++) {
		for (int j = 0; j < n; j++) {
			a[i][j] = matrix[i][j];
		}
	}
	double det = determinant(n);
	cout << "Determinant = " << det << endl;
	return 0;
}

#include <bits/stdc++.h>
using namespace std;

int countTriplets(int a[], int n) 
{
	unordered_set<int> s;
	for (int i = 0; i < n; i++)
		s.insert(a[i]);

	int count = 0;

	for (int i = 0; i < n-1; i++) {
		for (int j = i + 1; j < n; j++) {

		int xr = a[i] ^ a[j];

		if (s.find(xr) != s.end() && xr != a[i] && 
									xr != a[j])
			count++;
		}
	}

	return count / 3;
}

int main() 
{
	int a[] = {1, 3, 5, 10, 14, 15};
	int n = sizeof(a) / sizeof(a[0]); 
	cout << countTriplets(a, n); 
	return 0;
}

