#include <bits/stdc++.h> 
using namespace std; 

string addBinary(string A, string B) 
{ 
	if (A.length() > B.length()) 
		return addBinary(B, A); 

	int diff = B.length() - A.length(); 

	string padding; 
	for (int i = 0; i < diff; i++) 
		padding.push_back('0'); 

	A = padding + A; 
	string res; 
	char carry = '0'; 

	for (int i = A.length() - 1; i >= 0; i--) 
	{ 
		if (A[i] == '1' && B[i] == '1') 
		{ 
			if (carry == '1') 
				res.push_back('1'), carry = '1'; 
			else
				res.push_back('0'), carry = '1'; 
		} 

		else if (A[i] == '0' && B[i] == '0') 
		{ 
			if (carry == '1') 
				res.push_back('1'), carry = '0'; 
			else
				res.push_back('0'), carry = '0'; 
		} 

		else if (A[i] != B[i]) 
		{ 
			if (carry == '1') 
				res.push_back('0'), carry = '1'; 
			else
				res.push_back('1'), carry = '0'; 
		} 
	} 

	if (carry == '1') 
		res.push_back(carry); 
	reverse(res.begin(), res.end()); 

	int index = 0; 
	while (index + 1 < res.length() && 
		res[index] == '0') 
		index++; 
	return (res.substr(index)); 
} 

int main() 
{ 
	string a = "1101", b = "100"; 
	cout << addBinary(a, b) << endl; 
	return 0; 
}

#include <iostream> 
using namespace std; 

int binaryToDecimal(int n) 
{ 
	int num = n; 
	int dec_value = 0; 

	int base = 1; 

	int temp = num; 
	while (temp) { 
		int last_digit = temp % 10; 
		temp = temp / 10; 
		dec_value += last_digit * base; 
		base = base * 2; 
	} 

	return dec_value; 
} 

int main() 
{ 
	int num = 10101001; 
	cout << binaryToDecimal(num) << endl; 
}

