int gcd(int a, int b) {
    int res = min(a, b);

    while (res > 1) {
        if (a % res == 0 && b % res == 0)
            break;
        res--;
    }
    return res;
}

int main() {
    int a = 12, b = 16;

    cout << gcd(a, b);
    return 0;
}

